CREATE PROCEDURE [dbo].[get_claim_list_count]
	@id_admin int = null,
	@id_engeneer int = null,
	@date_start date = null,
	@date_end date = null
	as

 begin set nocount on;
	select id, name ,sys_name,order_num,background_color,foreground_color
	from claim_states where enabled=1 and (@sys_name is null or @sys_name = '' or (@sys_name is not null and @sys_name != '' and sys_name= @sys_name))
	and (@id is null or @id <=0 or (@id is not null and @id >0 and id= @id))

	end

CREATE PROCEDURE [dbo].[get_claim_state]
	@sys_name nvarchar(20) = null,
	@id int = null
	as

begin set nocount on;
select (select convert(decimal(10,2), value) from attributes where sys_name = 'CLSFRWAGE') as wage, (select convert(decimal(10,2), value) from attributes where sys_name = 'CLSFROVERHEAD') as overhead
end

CREATE PROCEDURE [dbo].[get_classifier_category]
	@id int = null,
	@number nvarchar(20) = null
	as

begin set nocount on;
select sid, login, password from mobile_users where enabled = 1
end

 begin set nocount on;
	select top 1 id, name ,sys_name,order_num,background_color,foreground_color
	from claim_states cs where enabled=1 and exists(select 1 from claim_states cs2 where cs2.enabled=1 and cs2.id=@id_claim_state and  cs.order_num > cs2.order_num)
	order by order_num
	end

begin set nocount on;
select s.id, c2cs.id_claim, c2cs.id as id_claim2claim_state, case when s.process_enabled=1 then 1 else 0 end as process_enabled, case when s.device_enabled=1 then 1 else 0 end as device_enabled, case when s.zip_claim=1 then 1 else 0 end as zip_claim, s.zip_claim_number, s.counter_mono, s.counter_color, counter_total, case when no_counter=1 then 1 else 0 end as no_counter, s.dattim1 as date_create, s.descr, case when counter_unavailable=1 then 1 else 0 end as counter_unavailable, counter_descr from service_sheet s inner join claim2claim_states c2cs on s.id=c2cs.id_service_sheet where s.enabled=1 and c2cs.enabled=1 and (@id is null or @id <= 0 or (@id is not null and @id > 0 and s.id=@id)) and (@id_claim is null or @id_claim <= 0 or (@id_claim is not null and @id_claim > 0 and c2cs.id_claim=@id_claim)) and (@id_claim2claim_state is null or @id_claim2claim_state <= 0 or (@id_claim2claim_state is not null and @id_claim2claim_state > 0 and c2cs.id=@id_claim2claim_state))
end

CREATE PROCEDURE [dbo].[get_service_sheet]
	@id int = null,
	@id_claim int = null,
	@id_claim2claim_state int = null
	as

begin set nocount on;
select id, id_parent, name, sys_name from work_types where (@id is null or @id <= 0 or (@id is not null and @id > 0 and id=@id)) and (@sys_name is null or @sys_name = '' or (@sys_name is not null and @sys_name != '' and enabled=1 and sys_name=@sys_name))
end

