CREATE PROCEDURE [dbo].[save_client_access]
@id int = null,
	@id_client_etalon int = null,
	@login nvarchar(50),
	--пароль приходит зашифрованый
	@password nvarchar(500),
	@creator_sid varchar(46),
	@ad_sid varchar(46) = null,
	@name nvarchar(500)=null,
	@full_name nvarchar(500)=null,
	@zip_access bit,
	@counter_access bit
AS

begin set nocount on;
if not exists(select 1 from contractor_access where id=@id) begin insert into contractor_access (login, password, creator_sid, ad_sid, name, org_name, city, org_sid, email) values (@login, @password, @creator_sid, @ad_sid, @name, @org_name, @city, @org_sid, @email) set @id = @@IDENTITY end
select @id as id end

