CREATE PROCEDURE [dbo].[get_other_employee_list]
@id_emp_state int,
@id_department int = null
	AS

	begin set nocount on;

	select vs_old.id, (select name from vendors v where v.id=vs_old.id_vendor) as vendor_name , vs_old.descr, vs_old.date_end, 
	(select name from organizations o where o.id=vs_old.id_organization) as organization_name, (select name from languages l where l.id=vs_old.id_language) as language, vs_old.pic_data, vs_old.creator_sid, vs_old.name
from vendor_states vs inner join vendor_states vs_old on vs.id=vs_old.old_id AND vs_old.id = (SELECT MAX(id) FROM vendor_states old WHERE old.old_id=@id)
where vs.id=@id 

	end

begin set nocount on;
select rh.id, rh.start_date, rh.end_date, rh.duration, case when can_edit=1 and confirmed=0 then 1 else 0 end as can_edit, case when confirmed=1 then 1 else 0 end as confirmed, employee_sid, e.display_name as employee_name, rh.year from rest_holidays rh inner join employees e on rh.employee_sid = e.ad_sid where rh.enabled=1 and (@employee_sid is null or @employee_sid = '' or (@employee_sid is not null and @employee_sid != '' and rh.employee_sid=@employee_sid)) and (@year is null or @year <=0 or (@year is not null and @year > 0 and year=@year)) order by employee_sid, year, start_date
end

CREATE PROCEDURE [dbo].[rest_holiday_list]
	@employee_sid varchar(46) = null,
	@year int
AS

begin set nocount on;
if (@can_edit is not null) begin update rest_holidays set can_edit=@can_edit where id in (select value from SplitInt(@id_array, ',')) and confirmed=0 end
if @confirmed is not null begin update rest_holidays set confirmed=@confirmed where id in (select value from SplitInt(@id_array, ',')) end end

CREATE PROCEDURE [dbo].[rest_holiday_list_confirm]
	@id_array nvarchar(max),
	@can_edit bit = null,
	@confirmed bit = null
AS

begin set nocount on;
if @year is null or @year <= 0 begin select @year = Year(@start_date) end
insert into rest_holidays(employee_sid, start_date,end_date,duration, can_edit,creator_sid, year) values(@employee_sid, @start_date,@end_date,@duration, @can_edit,@creator_sid, @year) end

