CREATE PROCEDURE [dbo].[close_doc_meet_link]
    @id INT = NULL ,
    @id_document INT = NULL ,
    @id_department INT = NULL ,
    @id_position INT = NULL ,
    @id_employee INT = NULL ,
    @deleter_sid VARCHAR(46)
AS

BEGIN SET NOCOUNT ON;
UPDATE documents SET ENABLED = 0, dattim2=GETDATE(), deleter_sid = @deleter_sid WHERE id=@id
UPDATE document_meet_links SET ENABLED = 0, dattim2=GETDATE(), deleter_sid = @deleter_sid WHERE ENABLED = 1 and id_document=@id END

