CREATE PROCEDURE [dbo].[save_holiday_work_confirm]	 
	@id_hw_document INT=null,
	@employee_sid VARCHAR(46)=null,
	@full_name nvarchar(150)
AS

BEGIN SET NOCOUNT ON;
INSERT INTO holiday_work_documents (date_start, date_end, creator_sid) VALUES (@date_start,@date_end, @creator_sid)
END

